// Create new governance proposal
const createProposal = async (proposalData) => {
  const proposal = {
    id: generateUUID(),
    title: proposalData.title,
    description: proposalData.description,
    proposer_id: proposalData.proposerId,
    voting_starts_at: new Date(Date.now() + 24*60*60*1000), // 24h delay
    voting_ends_at: new Date(Date.now() + 7*24*60*60*1000), // 7 days
    required_quorum: 0.10, // 10% participation
    status: 'draft'
  };
  return await database.proposals.create(proposal);
};

