const castVote = async (proposalId, memberId, choice, votingPower) => {
  // Create vote record
  const vote = {
    proposal_id: proposalId,
    member_id: memberId,
    vote_choice: choice, // 'for', 'against', 'abstain'
    voting_power_used: votingPower,
    cast_at: new Date()
  };

  // Optional: Record on Stellar for transparency
  const memo = `VOTE-${proposalId.slice(0,8)}-${choice.toUpperCase()}`;
  const transaction = createVotingTransaction(memo);

  return await database.votes.create(vote);
