// Execute treasury transfer
const executeTreasuryTransfer = async (proposalId, amount, asset, destination) => {
  const treasuryAccount = await server.loadAccount(TREASURY_PUBLIC_KEY);

  const transaction = new StellarSdk.TransactionBuilder(treasuryAccount, {
    fee: StellarSdk.BASE_FEE,
    networkPassphrase: StellarSdk.Networks.PUBLIC
  })
  .addOperation(StellarSdk.Operation.payment({
    destination: destination,
    asset: asset,
    amount: amount.toString()
  }))
  .addMemo(StellarSdk.Memo.text(`DAO-TREASURY-${proposalId.slice(0,8)}`))
  .setTimeout(180)
  .build();

  // Multi-signature required
  return transaction;
};
